import React from 'react';
import axios from 'axios';

const AdicionarCliente = () => {
  const adicionarCliente = async () => {
    const nomeCliente = document.getElementById('nome').value;
    const telefoneCliente = document.getElementById('numeroTelefone1').value;
    const telefoneCliente2 = document.getElementById('numeroTelefone2').value;
    const plataformaCliente = document.getElementById('plataforma').value;
    const emailCliente = document.getElementById('email').value;

    if (!nomeCliente || !telefoneCliente || !plataformaCliente || !emailCliente) {
      alert('Por favor, preencha todos os campos.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post(
        `${process.env.REACT_APP_API_URL}/clientes`,
        {
          nome: nomeCliente,
          numeroTelefone1: telefoneCliente,
          numeroTelefone2: telefoneCliente2,
          plataforma: plataformaCliente,
          email: emailCliente,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Adiciona lógica adicional aqui se necessário

    } catch (error) {
      console.error('Erro ao adicionar cliente:', error);
    }
  };

  return (
    <div>
      <a
        href="#"
        data-target="#adicionarClienteModal"
        data-toggle="modal"
        className="d-none d-sm-inline-block btn btn-sm btn shadow-sm"
        style={{ backgroundColor: 'green', color: 'white' }}
      >
        <i className="fas fa-user fa-sm text-white-50"></i> Adicionar Cliente
      </a>

      <div className="modal fade" id="adicionarClienteModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">
                Adicionar Cliente
              </h5>
              <button className="close" type="button" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">×</span>
              </button>
            </div>
            <div className="modal-body">
              <form id="adicionarClienteForm">
                {/* Restante do seu código ... */}
              </form>
            </div>
            <div className="modal-footer">
              <button className="btn btn-secondary" type="button" data-dismiss="modal">
                Cancelar
              </button>
              <button className="btn btn-success" onClick={adicionarCliente}>
                Adicionar
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdicionarCliente;
