import React from 'react';
import axios from 'axios';

const AdicionarCliente = () => {

    const adicionarCliente = async () => {

        const nomeCliente = document.getElementById('nome').value;
        const telefoneCliente = document.getElementById('numeroTelefone1').value;
        const telefoneCliente2 = document.getElementById('numeroTelefone2').value;
        const plataformaCliente = document.getElementById('plataforma').value;
        const emailCliente = document.getElementById('email').value;


        if (!nomeCliente || !telefoneCliente || !plataformaCliente || !emailCliente) {
            alert('Por favor, preencha todos os campos.');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            await axios.post(`${process.env.REACT_APP_API_URL}/clientes`, {
                nome: nomeCliente,
                numeroTelefone1: telefoneCliente,
                numeroTelefone2: telefoneCliente2,
                plataforma: plataformaCliente,
                email: emailCliente,
            }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });



        } catch (error) {
            console.error('Erro ao adicionar cliente:', error);

        }
    };

    return (
        <div>
            <a href="#" class="d-none d-sm-inline-block btn btn-sm btn shadow-sm" style={{ backgroundColor: 'green', color: 'white' }}><i
                class="fas fa-user fa-sm text-white-50"></i> Adicionar Cliente</a>
            <div class="modal fade" id="logoutModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
                aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Tem certeza?</h5>
                            <button class="close" type="button" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">×</span>
                            </button>
                        </div>
                        <div class="modal-body">Selecione "Sair" para encerrar a sessão.</div>
                        <div class="modal-footer">
                            <button class="btn btn-secondary" type="button" data-dismiss="modal">Cancelar</button>
                            <a class="btn btn-success" onClick={handleLogout}>Sair</a>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default AdicionarCliente;
