import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Main from '../components/Main';


const Cliente = () => {
    const { id } = useParams();
    const [cliente, setCliente] = useState(null);
    const [venda, setVenda] = useState(null);

    useEffect(() => {
        const fetchClienteDetalhes = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/clientes/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setCliente(response.data);
                fetchVendaDetalhes();
            } catch (error) {
                alert('Erro ao buscar detalhes do cliente:', error);
            }
        };
        const fetchVendaDetalhes = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/vendas/${cliente.venda.id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setVenda(response.data);
            } catch (error) {
                alert('Erro ao buscar detalhes da venda:', error);
            }
        };

        fetchClienteDetalhes();
        
    }, [id]);

    if (!cliente) {
        return <p>Carregando...</p>;
    }

    return (
        <Main>
            <div id="content-wrapper" class="d-flex flex-column">
                <div id="content">
                    <div class="container-fluid">
                        <h1 class="h3 mb-1 text-gray-800">{cliente.nome}</h1>
                        <span>ID: {cliente.id}</span>
                        <div class="row">
                            <div class="col-lg-6">
                                <div class="card position-relative">
                                    <div class="card-header py-3">
                                        <h6 class="m-0 font-weight-bold text-success">INFORMAÇÕES DO CLIENTE:</h6>
                                    </div>
                                    <div class="card-body">
                                        <form id="adicionarClienteForm">
                                            <div class="form-group">
                                                <label for="nome">Nome:</label>
                                                <input type="text" class="form-control" id="nome" name="nome" required />
                                            </div>
                                            <div class="form-group">
                                                <label for="numeroTelefone1">Telefone 1:</label>
                                                <input type="text" class="form-control" id="numeroTelefone1" name="numeroTelefone1" required />
                                            </div>
                                            <div class="form-group">
                                                <label for="numeroTelefone2">Telefone 2:</label>
                                                <input type="text" class="form-control" id="numeroTelefone2" name="numeroTelefone2" required />
                                            </div>
                                            <div class="form-group">
                                                <label for="plataforma">Plataforma:</label>
                                                <select class="form-control" id="plataforma" name="plataforma" required>
                                                    <option value="facebook">Facebook</option>
                                                    <option value="instagram">Instagram</option>
                                                    <option value="google">Google</option>
                                                </select>
                                            </div>
                                            <div class="form-group">
                                                <label for="email">Email:</label>
                                                <input type="email" class="form-control" id="email" name="email" required />
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                            <div class="col-lg-6">
                                <div class="card position-relative">
                                    <div class="card-header py-3">
                                        <h6 class="m-0 font-weight-bold text-success">VENDAS:</h6>
                                    </div>
                                    <div class="card-body">
                                        <div class="card border-success mb-3" >
                                            <div class="card-header bg-transparent border-success">Header</div>
                                            <div class="card-body text-success">
                                                <h5 class="card-title">Success card title</h5>
                                                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                            </div>
                                            <div class="card-footer bg-transparent border-success">Footer</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Main>
    );
};

export default Cliente;
