import React, { useEffect, useState } from 'react';
import axios from 'axios';
import moment from 'moment';
import { Line } from 'react-chartjs-2';

const ChartComponent = () => {
  const token = localStorage.getItem('token');
  const [vendasPendentes, setVendasPendentes] = useState([]);
  const [chartData, setChartData] = useState(null); // Adicione um estado para controlar os dados do gráfico

  useEffect(() => {
    const fetchVendasPendentes = async () => {
      try {
        let allVendasPendentes = [];
        let currentPage = 0;

        while (true) {
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/vendas`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
            params: {
              page: currentPage,
              size: 10,
            },
          });

          const vendasPendentes = response.data.content.filter(venda =>
            venda.status === 'PENDENTE' &&
            venda.data &&
            moment(new Date(...venda.data)).isAfter(moment().subtract(30, 'days'))
          );

          allVendasPendentes = allVendasPendentes.concat(vendasPendentes);

          if (response.data.last) {
            break;
          }

          currentPage++;
        }

        setVendasPendentes(allVendasPendentes);
        const chartData = {
          labels: ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul", "Ago", "Set", "Out", "Nov", "Dez"],
          datasets: [
            {
              label: '',
              data: vendasPendentes.map(venda => venda.valor),
              fill: true,
              borderColor: "#006400",
            },
          ],
        };
        setChartData(chartData);

      } catch (error) {
        console.error('Erro ao obter as vendas pendentes:', error);
      }
    };

    fetchVendasPendentes();
  }, [token]);

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
    elements: {
      point: {
        radius: 0,
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  };

  return (
    <div>
      {chartData && <Line data={chartData} options={options} />}
    </div>
  );
};

export default ChartComponent;
