            <div class="h5 mb-0 font-weight-bold text-gray-800">{qtdVendasUser}</div>
          </div>
          <div class="col-auto">
            <i class="fas fa-dollar-sign fa-2x text-gray-300"></i>
          </div>
        </div>
      </div>
    </div>
