import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Pagination } from 'react-bootstrap';
import Main from '../components/Main';

const VendasGeral = () => {
    const [clientes, setClientes] = useState([]);
    const [currentPage, setCurrentPage] = useState(0);
    const [clientesPerPage] = useState(10);

    useEffect(() => {
        const token = localStorage.getItem('token');

        const fetchData = async () => {
            try {
                console.log('Token:', token);  // Verifique se o token está correto
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/clientes?page=${currentPage}&size=${clientesPerPage}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setClientes(response.data.content);
            } catch (error) {
                console.error('Erro ao buscar clientes:', error);
            }
        };

        fetchData();
    }, [currentPage, clientesPerPage]);

    const paginate = (pageNumber) => setCurrentPage(pageNumber - 1);

    return (
        <Main>
            <div className="container-fluid">
                <h1 className="h3 mb-2 text-gray-800">Tabela Vendas Gerais</h1>

                <div className="card shadow mb-4">
                    <div className="card-header py-3">
                        <h6 className="m-0 font-weight-bold text-success">Clientes</h6>
                    </div>
                    <div className="card-body">
                        <div className="table-responsive">
                            <Table bordered hover>
                                <thead>
                                    <tr>
                                        <th>Nome</th>
                                        <th>Telefone 1</th>
                                        <th>Telefone 2</th>
                                        <th>Plataforma</th>
                                        <th>Email</th>
                                        <th>Vendas</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {clientes.map((cliente) => (
                                        <tr key={cliente.id}>
                                            <td>{cliente.nome}</td>
                                            <td>{cliente.numeroTelefone1}</td>
                                            <td>{cliente.numeroTelefone2}</td>
                                            <td>{cliente.plataforma}</td>
                                            <td>{cliente.email}</td>
                                            <td>{cliente.venda ? cliente.venda.length : 0}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </div>
                        <Pagination>
                            {Array.from({ length: response.data.totalPages }).map((_, index) => (
                                <Pagination.Item key={index + 1} active={index === currentPage} onClick={() => paginate(index + 1)}>
                                    {index + 1}
                                </Pagination.Item>
                            ))}
                        </Pagination>
                    </div>
                </div>
            </div>
        </Main>
    );
};

export default VendasGeral;
