import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Main from '../components/Main';


const Cliente = () => {
    const { id } = useParams();
    const [cliente, setCliente] = useState(null);
    const [vendas, setVendas] = useState([]);

    const [nome, setNome] = useState('');
    const [numeroTelefone1, setNumeroTelefone1] = useState('');
    const [numeroTelefone2, setNumeroTelefone2] = useState('');
    const [plataforma, setPlataforma] = useState('');
    const [email, setEmail] = useState('');

    const getStatusColor = (status) => {
        switch (status) {
            case 'PENDENTE':
                return 'gradient-warning';
            case 'CONVERTIDO':
                return 'gradient-success';
            case 'NAO_CONVERTIDO':
                return 'gradient-danger';
            default:
                return 'gradient-secondary';
        }
    };

    const updateCliente = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.put(`${process.env.REACT_APP_API_URL}/clientes/${cliente.id}`, {
                nome,
                numeroTelefone1,
                numeroTelefone2,
                plataforma,
                email,
            }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

        } catch (error) {
            alert('Erro ao adicionar cliente:', error);
        }
    };

    useEffect(() => {

        const handleInputChange = (event) => {
            const { name, value } = event.target;
    
            // Atualize os estados conforme necessário
            switch (name) {
                case 'nome':
                    setNome(value);
                    break;
                case 'numeroTelefone1':
                    setNumeroTelefone1(value);
                    break;
                case 'numeroTelefone2':
                    setNumeroTelefone2(value);
                    break;
                case 'plataforma':
                    setPlataforma(value);
                    break;
                case 'email':
                    setEmail(value);
                    break;
                default:
                    break;
            }
        };

        const fetchClienteDetalhes = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/clientes/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setCliente(response.data);
                setVendas(response.data.venda || []);
            } catch (error) {
                alert('Erro ao buscar detalhes do cliente:', error);
            }
        };

        fetchClienteDetalhes();
    }, [id]);

    if (!cliente) {
        return <p>Carregando...</p>;
    }

    return (
        <Main>
            <div id="content-wrapper" className="d-flex flex-column">
                <div id="content">
                    <div className="container-fluid" >
                        <span># {cliente.id}</span>
                        <h1 className="h3 mb-1 text-gray-800" style={{ textTransform: 'uppercase' }}>{cliente.nome}</h1>
                        <div className="row" style={{ marginTop: '20px' }}>
                            <div className="col-lg-6">
                                <div className="card position-relative">
                                    <div className="card-header py-3">
                                        <h6 className="m-0 font-weight-bold text-success">INFORMAÇÕES DO CLIENTE:</h6>
                                    </div>
                                    <div className="card-body">
                <form id="adicionarClienteForm">
                    <div className="form-group">
                        <label htmlFor="nome">Nome:</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="nome" 
                            name="nome" 
                            value={nome} 
                            onChange={handleInputChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="numeroTelefone1">Telefone 1:</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="numeroTelefone1" 
                            name="numeroTelefone1" 
                            value={numeroTelefone1} 
                            onChange={handleInputChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="numeroTelefone2">Telefone 2:</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="numeroTelefone2" 
                            name="numeroTelefone2" 
                            value={numeroTelefone2} 
                            onChange={handleInputChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="plataforma">Plataforma:</label>
                        <select 
                            className="form-control" 
                            id="plataforma" 
                            name="plataforma" 
                            value={plataforma} 
                            onChange={handleInputChange} 
                            required 
                        >
                            <option value="facebook">Facebook</option>
                            <option value="instagram">Instagram</option>
                            <option value="google">Google</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Email:</label>
                        <input 
                            type="email" 
                            className="form-control" 
                            id="email" 
                            name="email" 
                            value={email} 
                            onChange={handleInputChange} 
                            required 
                        />
                    </div>
                    <button 
                        className="btn btn-success" 
                        onClick={updateCliente} 
                    >
                        Salvar
                    </button>
                </form>
            </div>
                                </div>
                            </div>
                            <div className="col-lg-6">
                                <div className="card position-relative">
                                    <div className="card-header py-3">
                                        <h6 className="m-0 font-weight-bold text-success">VENDAS:</h6>
                                    </div>
                                    <div className="card-body">
                                        {vendas.map((venda) => (
                                            <div key={venda.id} className={`card border-${getStatusColor(venda.status)} mb-3`}>
                                                <div className={`card-header text-light bg-${getStatusColor(venda.status)} border-${getStatusColor(venda.status)}`}>{venda.status}
                                                    <a href="#" class="btn btn-transparent btn btn-sm" style={{ marginLeft: '10px', color: 'white' }}>
                                                        <i class="fas fa-edit"></i>
                                                    </a>
                                                </div>

                                                <div className={`card-body text-${getStatusColor(venda.status)}`}>
                                                    <h5 className="card-title">{venda.curso}</h5>
                                                    <p className="card-text text-dark ">Valor: R${venda.valor}</p>
                                                </div>
                                                <div className={`card-footer bg-transparent border-${getStatusColor(venda.status)}`}>Observações: {venda.obs}</div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Main>
    );
};

export default Cliente;
