import React, { useState } from 'react';
import axios from 'axios';

const AdicionarCliente = () => {
    const [isVendaModalOpen, setVendaModalOpen] = useState(false);
    const [statusVenda, setStatusVenda] = useState('PENDENTE');
    const [valorVenda, setValorVenda] = useState('');
    const [obsVenda, setObsVenda] = useState('');
    const [cursoVenda, setCursoVenda] = useState('Superior_Enfermagem');

    const adicionarCliente = async () => {

        const nomeCliente = document.getElementById('nome').value;
        const telefoneCliente = document.getElementById('numeroTelefone1').value;
        const telefoneCliente2 = document.getElementById('numeroTelefone2').value;
        const plataformaCliente = document.getElementById('plataforma').value;
        const emailCliente = document.getElementById('email').value;


        if (!nomeCliente || !telefoneCliente || !plataformaCliente || !emailCliente) {
            alert('Por favor, preencha todos os campos obrigatorios.');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            await axios.post(`${process.env.REACT_APP_API_URL}/clientes`, {
                nome: nomeCliente,
                numeroTelefone1: telefoneCliente,
                numeroTelefone2: telefoneCliente2,
                plataforma: plataformaCliente,
                email: emailCliente,
            }, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });



        } catch (error) {
            console.error('Erro ao adicionar cliente:', error);

        }
    };

    const adicionarVenda = async (idDoCliente) => {
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${process.env.REACT_APP_API_URL}/vendas/${idDoCliente}`,
                {
                    status: statusVenda,
                    valor: parseFloat(valorVenda),
                    obs: obsVenda,
                    curso: cursoVenda,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );


            setVendaModalOpen(false);
        } catch (error) {
            console.error('Erro ao adicionar venda:', error);
        }
    };

    return (
        <div>
            <a href="#"
                data-target="#adicionarClienteModal"
                data-toggle="modal"
                class="d-none d-sm-inline-block btn btn-sm btn shadow-sm"
                style={{ backgroundColor: 'green', color: 'white' }}>
                    <i
                    class="fas fa-user fa-sm text-white-50"></i> Adicionar Cliente</a>

            <div class="modal fade" id="adicionarClienteModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel"
                aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Adicionar Cliente</h5>
                            <button class="close" type="button" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">×</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <form id="adicionarClienteForm">
                                <div class="form-group">
                                    <label for="nome">Nome:</label>
                                    <input type="text" class="form-control" id="nome" name="nome" required />
                                </div>
                                <div class="form-group">
                                    <label for="numeroTelefone1">Telefone 1:</label>
                                    <input type="text" class="form-control" id="numeroTelefone1" name="numeroTelefone1" required />
                                </div>
                                <div class="form-group">
                                    <label for="numeroTelefone2">Telefone 2:</label>
                                    <input type="text" class="form-control" id="numeroTelefone2" name="numeroTelefone2" required />
                                </div>
                                <div class="form-group">
                                    <label for="plataforma">Plataforma:</label>
                                    <select class="form-control" id="plataforma" name="plataforma" required>
                                        <option value="facebook">Facebook</option>
                                        <option value="instagram">Instagram</option>
                                        <option value="google">Google</option>
                                    </select>
                                </div>
                                <div class="form-group">
                                    <label for="email">Email:</label>
                                    <input type="email" class="form-control" id="email" name="email" required />
                                </div>
                            </form>
                        </div>
                        <div class="modal-footer">
                            <button class="btn btn-secondary" type="button" data-dismiss="modal">Cancelar</button>
                            <button class="btn btn-success" onClick={() => setVendaModalOpen(true)}>Adicionar</button>
                        </div>
                    </div>
                </div>
            </div>
            {isVendaModalOpen && (
        <div className="modal fade" id="adicionarVendaModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">
                  Adicionar Venda
                </h5>
                <button className="close" type="button" onClick={() => setVendaModalOpen(false)} aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
              <div className="modal-body">
                <form>
                  <div className="form-group">
                    <label>Status:</label>
                    <select
                      className="form-control"
                      value={statusVenda}
                      onChange={(e) => setStatusVenda(e.target.value)}
                    >
                      <option value="PENDENTE">PENDENTE</option>
                      <option value="CONVERTIDO">CONVERTIDO</option>
                      <option value="NAO_CONVERTIDO">NAO_CONVERTIDO</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Valor:</label>
                    <input
                      type="number"
                      className="form-control"
                      value={valorVenda}
                      onChange={(e) => setValorVenda(e.target.value)}
                    />
                  </div>
                  <div className="form-group">
                    <label>Observação:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={obsVenda}
                      onChange={(e) => setObsVenda(e.target.value)}
                    />
                  </div>
                  <div className="form-group">
                    <label>Curso:</label>
                    <select
                      className="form-control"
                      value={cursoVenda}
                      onChange={(e) => setCursoVenda(e.target.value)}
                    >
                      <option value="Superior_Enfermagem">Superior_Enfermagem</option>
                      <option value="Pos_graduacao">Pos_graduacao</option>
                      <option value="Curso_Capacitacao">Curso_Capacitacao</option>
                      <option value="Tec_enfermagem">Tec_enfermagem</option>
                      <option value="Tec_radiologia">Tec_radiologia</option>
                      <option value="Tec_laboratorio">Tec_laboratorio</option>
                      <option value="Tec_segurancaDoTrabalho">Tec_segurancaDoTrabalho</option>
                    </select>
                  </div>
                </form>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" type="button" onClick={() => setVendaModalOpen(false)}>
                  Cancelar
                </button>
                <button className="btn btn-success" onClick={() => adicionarVenda(/* idDoCliente */)}>
                  Adicionar Venda
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
        </div>

    );
};

export default AdicionarCliente;
