import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import moment from 'moment';
import { Line } from 'react-chartjs-2';

const ChartComponent = () => {
  const token = localStorage.getItem('token');
  const [vendasPendentes, setVendasPendentes] = useState([]);
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchVendasPendentes = async () => {
      try {
        let allVendasPendentes = [];
        let currentPage = 0;

        while (true) {
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/vendas`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
            params: {
              page: currentPage,
              size: 10,
            },
          });

          const vendasPendentes = response.data.content.filter(
            (venda) =>
              venda.status === 'PENDENTE' &&
              venda.data &&
              moment(new Date(...venda.data)).isAfter(moment().subtract(30, 'days'))
          );

          allVendasPendentes = allVendasPendentes.concat(vendasPendentes);

          if (response.data.last) {
            break;
          }

          currentPage++;
        }

        setVendasPendentes(allVendasPendentes);
      } catch (error) {
        console.error('Erro ao obter as vendas pendentes:', error);
      }
    };

    fetchVendasPendentes();
  }, [token]);

  useEffect(() => {
    // Limpar o gráfico ao desmontar o componente
    return () => {
      if (chartRef.current) {
        chartRef.current.destroy();
      }
    };
  }, []);

  const chartData = {
    labels: [
      'Jan',
      'Fev',
      'Mar',
      'Abr',
      'Mai',
      'Jun',
      'Jul',
      'Ago',
      'Set',
      'Out',
      'Nov',
      'Dez',
    ],
    datasets: [
      {
        label: '',
        data: vendasPendentes.map((venda) => venda.valor),
        fill: true,
        borderColor: '#006400',
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
    elements: {
      point: {
        radius: 0,
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  };

  return (
    <div>
      {vendasPendentes.length > 0 && (
        <Line ref={chartRef} data={chartData} options={options} />
      )}
    </div>
  );
};

export default ChartComponent;
